mapper.select:
select *
from contractdetails


        and id = #{id}


        and cust_code = #{custCode}


        and code = #{code}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and term = #{term}


        and total_money = #{totalMoney}


        and status = #{status}


        and type = #{type}


        and operator_code = #{operatorCode}


        and operator_name = #{operatorName}


        and update_time = #{updateTime}


        and create_time = #{createTime}

mapper.update:
update contractdetails
cust_code = #{custCode}, contract_name=#{contractName}, start_time = #{startTime,jdbcType=DATE}, end_time = #{endTime,jdbcType=DATE}, term = #{term}, total_money = #{totalMoney}, status = #{status}, type = #{type}, memo=#{memo}, manager_code=#{managerCode}, operator_code = #{operatorCode}, operator_name = #{operatorName}, update_time = #{updateTime}, create_time = #{createTime},
where code = #{code}

update contractdetails
set status      =#{status},
    update_time =#{updateTime,jdbcType=TIMESTAMP}
where code = #{code}

mapper.select:
select a.*,b.cust_name,b.cust_type,b.id_type,b.id_num
from contractdetails a left join customer b on a.cust_code =b.code


        and a.contract_name like concat("%",#{contractQueryParam.contractName},"%")


        and b.cust_name like concat("%",#{contractQueryParam.custName},"%")


        and b.id_type =#{contractQueryParam.idType}


        and b.id_num like concat("%",#{contractQueryParam.idNum},"%")


        and a.status = #{contractQueryParam.status}


        and a.end_time  >= #{contractQueryParam.startEndTime}


        and a.end_time  <= #{contractQueryParam.endEndTime}


        and a.operator_name like concat("%",#{contractQueryParam.operatorName},"%");




                and  date_format(a.end_time,'%Y-%m-%d') <= #{contractQueryParam.curTime,jdbcType=TIMESTAMP}


                and  date_format(a.end_time,'%Y-%m-%d') >= #{contractQueryParam.curTime,jdbcType=TIMESTAMP}





order by a.update_time Desc

select a.*,b.cust_name,b.cust_type,b.id_type,b.id_num,b.phone from contractdetails a left join customer b on a.cust_code = b.code where
a.create_time >= date_add(#{curTime,jdbcType=TIMESTAMP}, interval -${contractTimeLimit} DAY) and a.create_time <= #{curTime,jdbcType=TIMESTAMP}
order by a.create_time DESC

select a.*,b.cust_name,b.cust_type,b.id_type,b.id_num,b.phone from contractdetails a left join customer b on a.cust_code = b.code where
a.end_time <= date_add(#{curTime,jdbcType=TIMESTAMP}, interval ${contractTimeLimit} DAY) and a.end_time >= #{curTime,jdbcType=TIMESTAMP}
and a.status = '01' order by a.end_time DESC

select a.*,b.cust_name,b.cust_type,b.id_type,b.id_num,b.phone from contractdetails a left join customer b on a.cust_code = b.code where
a.end_time >= date_add(#{curTime,jdbcType=TIMESTAMP}, interval -${hasOvercontractTimeLimit} DAY) and a.end_time <= #{curTime,jdbcType=TIMESTAMP}
and a.status = '01' order by a.end_time DESC
