mapper.select:
select *
from customer
order by create_time desc;

select *
from customer
where id_type =#{idType} and cust_type =#{custType} and id_num =#{idNum}#{idType}

select *
from customer

    and  status in ('01')

        and id_type = #{customerQueryParam.idType}


        and cust_name  like concat("%",#{customerQueryParam.custName},"%")


        and id_num =#{customerQueryParam.idNum}


        and profession  like concat("%",#{customerQueryParam.profession},"%")


          and create_time  >= #{param1.createTimeStart}


        and create_time   <= #{param1.createTimeEnd}


order by create_time desc,id_type
