mapper.select:
select  *
from sms_details


        and id = #{id}


        and code = #{code}


        and status = #{status}


        and cust_code = #{custCode}


        and sms_template_code = #{smsTemplateCode}


        and tel_phone = #{telPhone}


        and template_param = #{templateParam}


        and content = #{content}


        and sms_status = #{smsStatus}


        and ssm_id = #{ssmId}


        and Consumption = #{consumption}


        and tmoney = #{tmoney}


        and sms_send = #{smsSend}


        and dsc = #{dsc}


        and update_time = #{updateTime}


        and create_time = #{createTime}


        and operate_name = #{operateName}

mapper.update:
update sms_details
code = #{code}, status = #{status}, cust_code = #{custCode}, sms_template_code = #{smsTemplateCode}, tel_phone = #{telPhone}, template_param = #{templateParam}, content = #{content}, sms_status = #{smsStatus}, ssm_id = #{ssmId}, Consumption = #{consumption}, tmoney = #{tmoney}, sms_send = #{smsSend}, dsc = #{dsc}, update_time = #{updateTime}, create_time = #{createTime}, operate_name = #{operateName},
where code = #{code}
